/* ========================================
   Enhanced Settings Modal Styles
   ======================================== */
.settings-modal {
	width: min(480px, 90vw);
	padding: 0;
	background: var(--panel);
	border: 1px solid var(--border);
	box-shadow: 0 25px 50px -12px rgba(0, 0, 0, 0.5);
	overflow: visible;
}

.settings-modal .modal-header {
	padding: 20px 24px;
	border-bottom: 1px solid var(--border);
	background: rgba(255, 255, 255, 0.02);
	border-radius: var(--radius) var(--radius) 0 0;
	margin-bottom: 0;
}

.settings-modal .modal-header h2 {
	font-size: 18px;
	display: flex;
	align-items: center;
	gap: 10px;
	color: var(--text);
}

.settings-modal .modal-header h2 i {
	color: var(--accent);
}

.settings-body {
	padding: 24px;
	display: flex;
	flex-direction: column;
	gap: 20px;
}

.settings-item label {
	display: flex;
	justify-content: space-between;
	align-items: center;
	margin-bottom: 8px;
	font-size: 14px;
	color: var(--text);
	font-weight: 500;
}

.badge-mini {
	font-size: 10px;
	padding: 2px 6px;
	border-radius: 4px;
	background: rgba(124, 58, 237, 0.15);
	color: #d8b4fe;
	border: 1px solid rgba(124, 58, 237, 0.3);
	font-weight: 600;
}

.input-wrapper,
.select-wrapper {
	position: relative;
	display: flex;
	align-items: center;
}

.input-icon {
	position: absolute;
	left: 14px;
	color: var(--muted);
	font-size: 16px;
	pointer-events: none;
	z-index: 2;
}

.settings-modal input[type="text"],
.settings-modal input[type="password"],
.settings-modal select {
	width: 100%;
	padding: 12px 12px 12px 42px;
	background: var(--card);
	border: 1px solid var(--border);
	border-radius: 10px;
	color: var(--text);
	font-size: 14px;
	transition: all 0.2s ease;
	min-height: 46px;
	appearance: none;
}

.settings-modal select {
	cursor: pointer;
}

.settings-modal input:focus,
.settings-modal select:focus {
	border-color: var(--accent);
	box-shadow: 0 0 0 3px rgba(124, 58, 237, 0.15);
	background: rgba(255, 255, 255, 0.03);
}

.select-arrow {
	position: absolute;
	right: 14px;
	color: var(--muted);
	pointer-events: none;
	font-size: 18px;
}

.field-hint {
	margin: 6px 0 0;
	font-size: 12px;
	color: var(--muted);
	line-height: 1.4;
}

.modal-footer {
	padding: 20px 24px;
	border-top: 1px solid var(--border);
	background: rgba(255, 255, 255, 0.02);
	border-radius: 0 0 var(--radius) var(--radius);
}

/* Custom Scrollbar for Select Options (Firefox) */
select {
	scrollbar-width: thin;
	scrollbar-color: var(--accent) var(--card);
}
